import React from "react";
import MyContext from "./MyContext";
import { useEffect, useState } from "react";
import "./App.css";
import Counter from "./components/singleplayer/question/Counter";
import Timer from "./components/singleplayer/question/Timer";
import QuestionTimer from "./components/singleplayer/question/QuestionTimer";
import QuestionCounter from "./components/singleplayer/question/QuestionCounter";

const MyProvider = ({ children }) => {
  const [questions, setQuestions] = useState();
  const [message, setMessage] = useState();
  const [color, setColor] = useState();
  const [wrightAnswer, setWrightAnswer] = useState();
  const [score, setScore] = useState(0);
  const [newQuestion, setNewQuestion] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [g, setG] = useState(false);

  return (
    <MyContext.Provider
      value={{
        questions,
        setQuestions,
        message,
        setMessage,
        color,
        setColor,
        wrightAnswer,
        setWrightAnswer,
        score,
        setScore,
        newQuestion,
        setNewQuestion,
        gameOver,
        setGameOver,
        g,
        setG,
      }}
    >
      {children}
    </MyContext.Provider>
  );
};

export default MyProvider;
