import React from "react";
import useFetch from "../../hooks/useFetch";
import { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";

import MyContext from "../../context/MyContext";
import { Link } from "react-router-dom";
import Nav from "./Nav";

const Settings = () => {
  const context = useContext(MyContext);
  const {
    number,
    setNumber,
    loading,
    indexCounter,
    setIndexCounter,
    eror,
    diff,

    setDiff,

    setCat,
  } = context;

  const navigate = useNavigate();

  const f = (e) => {
    setDiff(e.target.value);
  };
  const x = (e) => {
    setCat(e.target.value);
  };

  const n = (e) => {
    setNumber(e.target.value);
  };

  // useEffect(() => {
  //     d &&
  //     setQuestions(d.map(e => e.d.results.question) )
  //       d &&  setIncorrect(d.results.incorrectAnswers)
  // }, [d, incorrect,setIncorrect,setQuestions,setDiff])

  // useEffect(() => {
  //     incorrect &&
  //         incorrect.length <= 3 && setAllAnswers(incorrect.push(d.results.correctAnswer))
  //     incorrect && setAllAnswers(incorrect)

  // }, [incorrect])

  // function RandomArrayShuffle(array) {
  //     var currentIndex = array.length,
  //         temporaryValue,
  //         randomIndex;

  //     while (0 !== currentIndex) {
  //         randomIndex = Math.floor(Math.random() * currentIndex);
  //         currentIndex -= 1;
  //         temporaryValue = array[currentIndex];
  //         array[currentIndex] =
  //             array[randomIndex];
  //         array[randomIndex] = temporaryValue;
  //     }
  //     return array;
  // }

  // allAnswers && RandomArrayShuffle(allAnswers)

  // useEffect(() => {
