import React from "react";
import MyContext from "../../../context/MyContext";
import Counter from "./Counter";
import QuestionCounter from "./QuestionCounter";
import QuestionTimer from "./QuestionTimer";
import Timer from "./Timer";
import { useContext, useState, useEffect } from "react";
import arrayRandomize from "../../../hooks/arrayRandomize";
import "../../../styling/questionBodyWithTime.css";
import Rewards from "../rewards/Rewards";
import { Link } from "react-router-dom";
import UseHint from '../../../hooks/UseHint'


const QuestionBody = () => {
  const context = useContext(MyContext);
  const {
    hints,
    loading,
    eror,
    results,
    randomAnswers,
    setRandomAnswers,
    score,
    setScore,
  } = context;


  const [selected, setSelected] = useState();
  const [questionArray, setQuestionArray] = useState();
  const [wrongAnswers, setWrongAnswers] = useState();
  const [rightAnswer, setRightAnswer] = useState();
  const [answers, setAnswers] = useState([])

  const [indexCounter, setIndexCounter] = useState(0);


  useEffect(() => {
  results.map((item) => item.question);

  },[results])

  useEffect(() => {
      results.map((item) => item.incorrectAnswers);

  },[results])

  useEffect(() => {
      results.map((item) => item.correctAnswer);

  },[results])



  // console.log(questionArray);
  // console.log(wrongAnswers);
  // console.log(rightAnswer);

  const handleSelect = (i) => {
    if (selected === i && selected === rightAnswer[indexCounter])
      return "select";
    else if (selected === i && selected !== rightAnswer[indexCounter])
      return "wrong";
    else if (i === rightAnswer[indexCounter]) return "select";
  };
  const handleCheck = (i) => {
    setSelected(i);
    if (i === rightAnswer[indexCounter]) setScore(score + 10);
    setError(false);
  };
  hints === 1 && console.log("SKJNSLN");
  const nextHandler = () => {
    // console.log("first");
    if (selected) {
      setSelected();
    } else setError("Please select an option first");

    setIndexCounter((prevIndexCounter) => prevIndexCounter + 1);
  };
  // console.log(rightAnswer[indexCounter]);

  const answer = [];
  answer.push(rightAnswer[indexCounter]);
  wrongAnswers[indexCounter].map((el) => answer.push(el));

  useEffect(() => {
    setAnswers(answer)


  }, [randomAnswers])
  console.log(answers);

  useEffect(() => {
    setRandomAnswers(arrayRandomize(answers));
  }, [indexCounter, setRandomAnswers, randomAnswers]);


  console.log(randomAnswers);

  if (loading) return <p>loading ..</p>;
  if (eror) return <p>{eror}</p>;

  return (
    <main>


      <div className="App">
        <header className="App-header">
          <div className="rewards">


            <Rewards />
          </div>

          <div
            className="quest-sec">
            Q{indexCounter + 1} . {questionArray[indexCounter]}
          </div>
          <div className="ans-sec">
            {randomAnswers.map((el, index) => (
              <div className="align-items">
                <button
                  name={el}
                  value={el}
                  className={`singleOption  ${selected && handleSelect(el)}`}
                  key={el}
                  onClick={() => handleCheck(el)}
                  disabled={selected}

                >
                  {index + 1 + "." + el}
                </button>
              </div>
            ))}
          </div>
          <button onClick={() => setAnswers(answers.pop())}>hint</button>

          <button onClick={nextHandler}>next</button>
          {/* <Link to="timeMode"> <button on>TIMeMOde !!!</button> </Link> */}
          {/* {hints = 1 && <button></button> } */}
        </header>

      </div>
      <QuestionCounter />
      {/* <QuestionTimer /> */}
      <Timer />

      {/* { questions && <p>{questions}</p> } */}
    </main>
  );
};

export default QuestionBody;
