function shuffle(array) {
  var currentIndex = array.incorrectAnswers.length,
  			temporaryValue,
  			randomIndex;

  		while (0 !== currentIndex) {
  			randomIndex = Math.floor(Math.random() * currentIndex);
  			currentIndex -= 1;
  			temporaryValue = array.incorrectAnswers[currentIndex];
  			array.incorrectAnswers[currentIndex] =
  				array.incorrectAnswers[randomIndex];
  			array.incorrectAnswers[randomIndex] = temporaryValue;
  		}
  		return array;
  	}
  export default shuffle;
