import React from "react";
import useFetch from "../../hooks/useFetch";
import { useState, useEffect, useContext } from "react";
import {useNavigate} from "react-router-dom"

import MyContext from "../../context/MyContext";
import { Link } from "react-router-dom";

const Settings = () => {

  const context = useContext(MyContext);
  const {
    number,
    setNumber,
    loading,
    indexCounter,
    setIndexCounter,
    eror,
    results,
    incorrect,
    setIncorrect,
    allAnswers,
    setAllAnswers,
    questions,
    setQuestions,
    correctAnswer,
    setCorrectAnswer,
    diff,
    question,
    setQuestion,
    setDiff,
    cat,
    setCat,
    initialState,
  } = context;

  const navigate = useNavigate();


  const f = (e) => {
    setDiff(e.target.value);
  };
  const x = (e) => {
    setCat(e.target.value);
  };
  const n = (e) => {   
    setNumber(e.target.value)


  };

  console.log(diff);
  if (loading) return <p>loading ..</p>;
  if (eror) return <p>{eror}</p>;

  return (
    <div>
      <h1>ChOOSE SETTINGS</h1>
      <Link to="/questions">
        <button>PLAY</button>
      </Link>

      <div className="settings">
        <label>Difficulty</label>

        {
          <select onChange={(e) => f(e)}>
            <option onChange={(e) => f(e)} value="easy">
              easy
            </option>
            <option onChange={(e) => f(e)} value="medium">
              medium
            </option>
            <option onChange={(e) => f(e)} value="hard">
              hard
            </option>
          </select>
        }
        <label>Categories</label>
        {
          <select onChange={(e) => x(e)}>
            <option onChange={(e) => x(e)} value="Arts">
              Arts & Literature
            </option>
            <option onChange={(e) => x(e)} value="Film">
              Film & TV
            </option>
            <option onChange={(e) => x(e)} value="Food">
              Food & Drink
            </option>
            <option onChange={(e) => x(e)} value="General Knowledge">
              General Knowledge
            </option>

            <option onChange={(e) => x(e)} value="Geography">
              Geography
            </option>

            <option onChange={(e) => x(e)} value="History">
              History
            </option>

            <option onChange={(e) => x(e)} value="Music">
              Music
            </option>

            <option onChange={(e) => x(e)} value="Science">
              Science
            </option>

            <option onChange={(e) => f(e)} value="Society">
              Society & Culture{" "}
            </option>
            <option onChange={(e) => f(e)} value="Sport">
              Sport & Leisure{" "}
            </option>
          </select>
        }
     <label htmlFor="number">Amount of Questions</label>
        <input className='amount'  type="number" name='number' min="1" max="3" required="required" onChange={(e) => n(e)}  value={number}>
        </input>
        <button ></button>t
      </div>
    </div>
  );
};

export default Settings;
